svg.script:

const circle = document.getElementsByTagName("circle")[0];
const moveSpeed = 5;
let x = circle.getAttribute('cx')*1;
let y = circle.getAttribute('cy')*1;
document.addEventListener('keydown', function(event) {
  switch(event.key) {
  case 'w':
    circle.setAttribute('cy', y-=moveSpeed);
    break;
  case 'a':
    circle.setAttribute('cx', x-=moveSpeed);
    break;
  case 's':
    circle.setAttribute('cy', y+=moveSpeed);
    break;
  case 'd':
    circle.setAttribute('cx', x+=moveSpeed);
    break;
  }
});
